def filter_switch_controller_global_data(json):
    option_list = [
        "allow_multiple_interfaces",
        "bounce_quarantined_link",
        "custom_command",
        "default_virtual_switch_vlan",
        "dhcp_server_access_list",
        "disable_discovery",
        "fips_enforce",
        "firmware_provision_on_authorization",
        "https_image_push",
        "log_mac_limit_violations",
        "mac_aging_interval",
        "mac_event_logging",
        "mac_retention_period",
        "mac_violation_timer",
        "quarantine_mode",
        "sn_dns_resolution",
        "update_user_device",
        "vlan_all_mode",
        "vlan_optimization",
    ]

    json = remove_invalid_fields(json)
    dictionary = {}

    for attribute in option_list:
        if attribute in json and json[attribute] is not None:
            dictionary[attribute] = json[attribute]

    return dictionary


def flatten_single_path(data, path, index):
    if (
        not data
        or index == len(path)
        or path[index] not in data
        or not data[path[index]]
    ):
        return

    if index == len(path) - 1:
        data[path[index]] = " ".join(str(elem) for elem in data[path[index]])
    elif isinstance(data[path[index]], list):
        for value in data[path[index]]:
            flatten_single_path(value, path, index + 1)
    else:
        flatten_single_path(data[path[index]], path, index + 1)


def flatten_multilists_attributes(data):
    multilist_attrs = [
        ["update_user_device"],
    ]

    for attr in multilist_attrs:
        flatten_single_path(data, attr, 0)

    return data


def underscore_to_hyphen(data):
    if isinstance(data, list):
        for i, elem in enumerate(data):
            data[i] = underscore_to_hyphen(elem)
    elif isinstance(data, dict):
        new_data = {}
        for k, v in data.items():
            new_data[k.replace("_", "-")] = underscore_to_hyphen(v)
        data = new_data

    return data


def switch_controller_global(data, fos):
    vdom = data["vdom"]
    switch_controller_global_data = data["switch_controller_global"]
    switch_controller_global_data = flatten_multilists_attributes(
        switch_controller_global_data
    )
    filtered_data = underscore_to_hyphen(
        filter_switch_controller_global_data(switch_controller_global_data)
    )

    return fos.set("switch-controller", "global", data=filtered_data, vdom=vdom)


def is_successful_status(resp):
    return (
        "status" in resp
        and resp["status"] == "success"
        or "http_status" in resp
        and resp["http_status"] == 200
        or "http_method" in resp
        and resp["http_method"] == "DELETE"
        and resp["http_status"] == 404
    )


def fortios_switch_controller(data, fos):

    fos.do_member_operation("switch-controller", "global")
    if data["switch_controller_global"]:
        resp = switch_controller_global(data, fos)
    else:
        fos._module.fail_json(
            msg="missing task body: %s" % ("switch_controller_global")
        )

    return (
        not is_successful_status(resp),
        is_successful_status(resp)
        and (resp["revision_changed"] if "revision_changed" in resp else True),
        resp,
        {},
    )


