def filter_web_proxy_explicit_data(json):
    option_list = [
        "ftp_incoming_port",
        "ftp_over_http",
        "http_incoming_port",
        "https_incoming_port",
        "https_replacement_message",
        "incoming_ip",
        "incoming_ip6",
        "ipv6_status",
        "message_upon_server_error",
        "outgoing_ip",
        "outgoing_ip6",
        "pac_file_data",
        "pac_file_name",
        "pac_file_server_port",
        "pac_file_server_status",
        "pac_file_through_https",
        "pac_file_url",
        "pac_policy",
        "pref_dns_result",
        "realm",
        "sec_default_action",
        "socks",
        "socks_incoming_port",
        "ssl_algorithm",
        "status",
        "strict_guest",
        "trace_auth_no_rsp",
        "unknown_http_version",
    ]

    json = remove_invalid_fields(json)
    dictionary = {}

    for attribute in option_list:
        if attribute in json and json[attribute] is not None:
            dictionary[attribute] = json[attribute]

    return dictionary


def flatten_single_path(data, path, index):
    if (
        not data
        or index == len(path)
        or path[index] not in data
        or not data[path[index]]
    ):
        return

    if index == len(path) - 1:
        data[path[index]] = " ".join(str(elem) for elem in data[path[index]])
    elif isinstance(data[path[index]], list):
        for value in data[path[index]]:
            flatten_single_path(value, path, index + 1)
    else:
        flatten_single_path(data[path[index]], path, index + 1)


def flatten_multilists_attributes(data):
    multilist_attrs = [
        ["outgoing_ip"],
        ["outgoing_ip6"],
    ]

    for attr in multilist_attrs:
        flatten_single_path(data, attr, 0)

    return data


def underscore_to_hyphen(data):
    if isinstance(data, list):
        for i, elem in enumerate(data):
            data[i] = underscore_to_hyphen(elem)
    elif isinstance(data, dict):
        new_data = {}
        for k, v in data.items():
            new_data[k.replace("_", "-")] = underscore_to_hyphen(v)
        data = new_data

    return data


def web_proxy_explicit(data, fos):
    vdom = data["vdom"]
    web_proxy_explicit_data = data["web_proxy_explicit"]
    web_proxy_explicit_data = flatten_multilists_attributes(web_proxy_explicit_data)
    filtered_data = underscore_to_hyphen(
        filter_web_proxy_explicit_data(web_proxy_explicit_data)
    )

    return fos.set("web-proxy", "explicit", data=filtered_data, vdom=vdom)


def is_successful_status(resp):
    return (
        "status" in resp
        and resp["status"] == "success"
        or "http_status" in resp
        and resp["http_status"] == 200
        or "http_method" in resp
        and resp["http_method"] == "DELETE"
        and resp["http_status"] == 404
    )


def fortios_web_proxy(data, fos):

    fos.do_member_operation("web-proxy", "explicit")
    if data["web_proxy_explicit"]:
        resp = web_proxy_explicit(data, fos)
    else:
        fos._module.fail_json(msg="missing task body: %s" % ("web_proxy_explicit"))

    return (
        not is_successful_status(resp),
        is_successful_status(resp)
        and (resp["revision_changed"] if "revision_changed" in resp else True),
        resp,
        {},
    )


